// SPDX-License-Identifier: GPL-3.0

pragma solidity >=0.7.0 <0.9.0;

contract Auction {
    address public highestBidder; // The highest bidder's address
    address public owner;
    uint public highestBid; // The amount of the highest bid
    mapping(address => uint) public userBalances; // mapping for the amount to return

    constructor() payable{
        owner = msg.sender;
        highestBidder = owner;
        highestBid = 0;
    }

    function bid() public payable{
        require(msg.sender != owner, "Owner can't send a bid!");
        require(msg.value > highestBid, "The bid is less than the current highest bid!");
        highestBid = msg.value;
        highestBidder = msg.sender;
        userBalances[msg.sender] = msg.value;
    }

    function withdraw() public payable{
        require(userBalances[msg.sender] > 0, "The amount to return is zero!");
        require(userBalances[msg.sender]!=highestBid, "The highestBidder can't take back his bid!");
        payable(msg.sender).transfer(userBalances[msg.sender]);
        userBalances[msg.sender] = 0;
    }

    receive() external payable{}
}
